package ex1.io;

public class Program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.write(3); //3이라는 코드값에 해당하는 문자를 write 한 것
		System.out.write('3'+1); //그 다음 코드가 나온다.
		System.out.write('3'+2);
		System.out.write('3'+3);
		System.out.write('A'+0); 
		System.out.write('A'+1); //A라는 기호에 해당하는 코드값에 1을 더한 값
		System.out.write('A'+2);
		System.out.write('A'+3);
		
		System.out.flush();
	}

}
